        for(int i=0; i<s.length(); i++) {
            string s1 = s.substr(0, i+1); //One half
            string s2 = s.substr(i+1); //Other half

            //Check for 1st half in the trie and if it is present in the trie then recurse for next half
            if(search(s1) && solve(s2, cnt+1)) 
                return true;
        }

        return false; //If not found concatenated words
    }

