		# Time O(n*n) Space O(n)
		# dp - using word break I problem logic
		def dp(s):
			n = len(s)

			dp = [False] * (n+1)
			dp[0] = True

			for i in range(1,n+1):
				for j in range(i):
					if dp[j] and s[j:i] in word_set and s[j:i] != s:
						dp[i] = True
						break

			return dp[n]

		result = []
		word_set = set(words)

		for word in word_set:
			if word and dp(word):
				result.append(word)

