**LOGIC** :  Count the number of words while doing dfs, if the result ends with a trie object with isend = true and the words count is greater than 1(we didnt find the word itself in the trie, i.e,  we didn't match 'cat' with 'cat' but with 'c' and 'at' maybe) then we reten true;

```
class Trie {
    public:
    vector<Trie*> next;
    bool isend;
    Trie(){
        isend = false;
        next.resize(26);
    }
};

class Solution {
public:
    void addword(Trie* head, string &w)
    {
        for(int i = 0; i < w.length(); i++)
        {
            if(!head->next[w[i]-'a']) head->next[w[i]-'a'] = new Trie();
            head = head->next[w[i]-'a'];
        }
        head->isend = true;
    }

    bool searchword(Trie* head, string &w, int idx = 0, int words = 0)
    {
        Trie* curr = head;

        for(int i = idx; i < w.length(); i++)
        {
            if(!head->next[w[i]-'a']) return false;
            head = head->next[w[i]-'a'];
            if(i != w.length()-1 && head->isend && searchword(curr, w, i+1, words+1)) return true;

        }
        words+=1;
        return words > 1 && head->isend;
    }

    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        Trie* head = new Trie();
        vector<string> ans;
        for(string w : words)
        {
            addword(head, w);
        }
        for(string w : words)
        {
            if(searchword(head, w)) ans.push_back(w);
        }
        return ans;
    }
};
