class Solution {
public:
    struct TrieNode {
        unordered_map<char, TrieNode*> children;
        bool isWord;
    };

    TrieNode* root = new TrieNode();

    void insert(string word) {
        TrieNode* node = root;
        for (char c : word) {
            if (!node->children.count(c)) {
                node->children[c] = new TrieNode();
            }
            node = node->children[c];
        }
        node->isWord = true;
    }

    bool check(string word, int start, int count, TrieNode* node) {
        if (start == word.size()) {
            return count >= 2;
        }
        if (!node->children.count(word[start])) {
            return false;
        }
        TrieNode* next = node->children[word[start]];
        if (next->isWord && check(word, start + 1, count + 1, root)) {
            return true;
        }
        return check(word, start + 1, count, next);
    }

    vector<string> findAllConcatenatedWordsInADict(vector<string>& words) {
        for (string word : words) {
            insert(word);
        }
        vector<string> result;
        for (string word : words) {
            if (check(word, 0, 0, root)) {
                result.push_back(word);
            }
        }
        return result;
    }
};
