        for(int i=0; i<ms.length;++i){
            for(int mm = m; mm>=ms[i];--mm){
                for(int nn=n; nn>=ns[i];--nn){
                    dp[mm][nn]=Math.max(dp[mm][nn],dp[mm-ms[i]][nn-ns[i]]+1);
                }
            }
        }
        return dp[m][n];
    }
}
