* ***Using Recursion && Memoization***

* ***Time Complexity :- O(N * L * L), Where N is size of array and L is the max. size of string***

* ***Space Complexity :- O(N * L * L)***

```
class Solution {
public:

    // declare a 3D dp

    int dp[605][105][105];

    int helper(vector<pair<int, int>>& arr, int i, int n, int zeros, int ones)
    {
        // base case

        if(i == n)
        {
            return 0;
        }

        // if already calculated

        if(dp[i][zeros][ones] != -1)
            return dp[i][zeros][ones];

        // we have two options either include the curr string or exclude

        // inclusion part

        int include = 0; 

        if(zeros - arr[i].first >= 0 && ones - arr[i].second >= 0)
        {
            include = 1 + helper(arr, i + 1, n, zeros - arr[i].first, ones - arr[i].second);
        }

        // exclusion part

        int exclude = helper(arr, i + 1, n, zeros, ones);

        // store the res and return it

        return dp[i][zeros][ones] = max(include, exclude);
    }

    int findMaxForm(vector<string>& strs, int zeros, int ones) {

        int n = strs.size();

        // find the count of zero and one in every string and store in array

        vector<pair<int, int>> arr(n);

        for(int i = 0; i < n; i++)
        {
            int count_0 = 0;

            int count_1 = 0;

            for(int j = 0; j < strs[i].size(); j++)
            {
                if(strs[i][j] == '0')
                {
                    count_0++;
                }
                else
                {
                    count_1++;
                }
            }

            arr[i] = {count_0, count_1};
        }

        // initialize dp with -1

        memset(dp, -1, sizeof(dp));

        return helper(arr, 0, n, zeros, ones);
    }
};
```

