public int findMaxFormRec(String[] strs, int m, int n) {

	int len = strs.length;
	int[][] map = new int[len][2];

	for (int i = 0; i < len; i++) {
		String binary = strs[i];
		int count1 = 0, count2 = 0;
		for (char ch : binary.toCharArray()) {
			if (ch == '0')
				count1++;
			else
				count2++;
		}
		map[i][0] = count1;
		map[i][1] = count2;
	}

	return findMaxFormRecHelper(strs, m, n, 0, len, map);
}

