public int findMaxFormRecHelper(String[] strs, int m, int n, int idx, int len, int[][] map) {

	if (m < 0 || n < 0)
		return Integer.MIN_VALUE;

	if (idx == len)
		return 0;

	int yes = findMaxFormRecHelper(strs, m - map[idx][0], n - map[idx][1], idx + 1, len, map);
	int no = findMaxFormRecHelper(strs, m, n, idx + 1, len, map);

	if (yes != Integer.MIN_VALUE)
		yes += 1;

	return Math.max(yes, no);
}

