```
class Solution {
    public int findMaxForm(String[] strs, int m, int n) {
        // keeps track max size of the subset that could be stored for each max[i][j] where
        // i is the number of zeroes
        // j is the number of ones
        int[][] max = new int[m+1][n+1];
        for(String str : strs){
            int neededZeroes = 0;
            int neededOnes = 0;

            for(int i = 0; i < str.length(); i++){
                if(str.charAt(i) == '0') neededZeroes++;
                else neededOnes++;
            }

            for(int zero = m; zero >= 0; zero--){
                for(int one = n; one >= 0; one--){
                    // if we could take the current string then reassess the max
                    if(zero >= neededZeroes && one >= neededOnes){
                        // compare the size of subsets if we don't take it vs we take it
                        max[zero][one] = Math.max(max[zero][one], 1 + max[zero-neededZeroes][one-neededOnes]);
                    }else{
                        break;
                    }
                }
            }

        }

        return max[m][n];
    }
}
