	def findMaxForm(self, strs, m, n):
        """
        :type strs: List[str]
        :type m: int
        :type n: int
        :rtype: int
        """
        # m: 0 n: 1
        dp = [[0 for _ in range(n + 1)] for _ in range(m + 1)]

        for str in strs:
            zeros = 0
            ones = 0
            for char in str:
                if char == "0":
