class Solution {
public:
    int recurse(vector<string>& strs,int i,int m,int n,vector<vector<vector<int>>>&dp,int size,vector<int>&ones,vector<int>&zeros)
    {
        if(i==size ) return 0;
        if(dp[i][m][n]!=-1) return dp[i][m][n];
        int nt=recurse(strs,i+1,m,n,dp,size,ones,zeros);
        int t=0;
        if(m>=zeros[i] && n>=ones[i])
        {
            t=1+recurse(strs,i+1,m-zeros[i],n-ones[i],dp,size,ones,zeros);
        }
        return dp[i][m][n]=max(t,nt);
    }
