        int size=strs.size();
        vector<int>ones(size,0),zeros(size,0);
        string s;
        for(int i=0;i<size;i++)
        { 
           s=strs[i];
           for(char c:s)
           {
               if(c=='0') zeros[i]++;
               else ones[i]++;
           }   
