class Solution:
    def findMaxForm(self, strs: List[str], m: int, n: int) -> int:
        def count_digits(string: str) -> Tuple[int, int]:
			""" Count zeroes and ones in string

			Args:
				string: the input string
			Returns:
				int: count of zeroes in input string
				int: count of ones in input string
			"""
            m = 0
            for c in string:
                if c == "0":
                    m += 1
            return m, len(string) - m

        def backtracking(m, n, idx = 0, dp = {}):
			# we processed all count for input list of strings
            if idx == len(counts):
                return 0

			# the combination m, n, idx has been processed previously
            if (m, n, idx) in dp:
                return dp[(m, n, idx)]

			# try to find the max form without the string at index idx
            max_result = backtracking(m, n, idx + 1) 

            # try to find the max form with the string at index idx
            count_zeroes, count_ones = counts[idx]
            if m - count_zeroes >= 0 and n - count_ones >= 0:
                result = backtracking(m - count_zeroes, n - count_ones, idx + 1) + 1 
                max_result = max(max_result, result)

			# memoize the combination m, n, idx to avoid processing it again
            dp[(m, n, idx)] = max_result

            return max_result

        counts = [count_digits(s) for s in strs]
        return backtracking(m, n)
