    private int getMaxLengthSubset(int index, int[][] counts, int zeroes, int ones){
        if(index == counts.length){
            return 0;
        }

        //retrive from cache
        if(cache[index][zeroes][ones] != 0) return cache[index][zeroes][ones];


        //Aproach 
        //here we using the backtracking 
        //1. consider     :  add current binary string in subset,
        //2. not consider :  just ignore this current string, move to another 


        //this time consider, current binary string count
        int considered = 0;
        int remainingZeroes = zeroes - counts[index][0];
        int remainingOnes   = ones - counts[index][1];

        if(remainingZeroes >= 0 && remainingOnes >= 0){
            //call getMaxLengthSubset, pass the remaining Zeroes and ones
            considered = 1 + getMaxLengthSubset(index + 1, counts, remainingZeroes, remainingOnes);
        }

        //ignore this string
        //call getMaxLengthSubset, pass actual Zeroes and Ones
        int notConsidered = getMaxLengthSubset(index + 1, counts, zeroes, ones);

        //find max number subset store in the cache
        cache[index][zeroes][ones] = Math.max(considered, notConsidered);

        return cache[index][zeroes][ones];
    }

    private int[] getCount(String str){
        int[] count = new int[2];

        //count zeroes and ones of current string
        for(char c : str.toCharArray()){
            count[c - '0']++;
        }

        return count;
    }
}
