strs = ["001", "110","0000","0000"]
m = 9
n = 2

def go(strs,index,m,n,dp):
    if m<0 or n<0:
        return float('-inf')
    if index == len(strs):
        return 0
    if dp[index][m][n]!=-1:
        return dp[index][m][n]

    zero = strs[index].count('0')
    one = strs[index].count('1')
    if zero <= m and one <= n:
        dp[index][m][n] = max( 1 + go(strs,index+1,m-zero,n-one,dp),
                               go(strs,index+1,m,n,dp))
    else:
        dp[index][m][n] = go(strs,index+1,m,n,dp)

    return dp[index][m][n]


def one_zero(strs,m,n):
    dp  = [ [ [-1]*(n+1) for _ in range(m+1) ]  for _ in range(len(strs))]
    return go(strs,0,m,n,dp),dp

print(one_zero(strs,m,n))


# bottom-up DP
def ones_zeroes(strs,m,n):
    dp = [[0] * (n + 1) for _ in range(m + 1)]

    for s in strs:
        zero = s.count('0')
        one = s.count('1')

        for i in range(m,-1,-1):
            for j in range(n,-1,-1):
                if zero <= i and one <= j:
                    dp[i][j] = max( dp[i][j] ,1 + dp[i-zero][j-one])
                # else:
                #     dp[i][j] = dp[i][j]    # but don't care take or not

    return dp[-1][-1]

