class Solution {
public:
    int dp[601][101][101]; 
	// i is index,l is length
    int helper(int i,int m,int n,int l,vector<string>& strs){
        if(m==0 && n==0) // no more one and zero available
            return dp[i][m][n]= 0;
        if(i==l)  // end of vector
            return  dp[i][m][n]= 0;
        if( dp[i][m][n]!=-1){
            return  dp[i][m][n];
        }
        int nz =0, no = 0; //nz -number  of zero , no -number of one

    // count number of zeroes and ones in current string
    for (auto c : strs[i]) {
      if (c == '0')
	nz++;
      else if (c == '1')
	no++;
    }

       int a=-2;
        int b=-2;
      if(n>=no && m>=nz){ //we have required capacity of one and zeron to take this string 
         a= helper(i+1,m-nz,n-no,l,strs)+1;// profit is 1
      }   
     b=helper(i+1,m,n,l,strs); //we do not include this string

        return  dp[i][m][n]=max(a,b);



    }



    int findMaxForm(vector<string>& strs, int m, int n) {
        memset(dp,-1,sizeof(dp));
       return helper(0,m,n,strs.size(),strs);


    }
};
