Note : dp[i][m][n] indicates the largest subset you can form with m zeros and n ones till current index.

```
class Solution {
    static int[][][] dp;
//as they are three changing parameters we use 3D dp

    public int dfs(int i,int m,int n,String[] strs)
    { 
        if(i == 0)
        { //when we are at index 0 the largest subset with m zeros and n ones will be the count of ones and zeros in that string
            int zeros = 0,ones = 0;
            for(char c : strs[0].toCharArray())
            {
               if(c == '0') zeros++;
               else ones++;
            }
              //zeros and ones are valid return 1,else return 0
             if(zeros <= m && ones <= n)
             return 1;

             return 0;
        }
        //already cached return the respective value
        if(dp[i][m][n] != -1) return dp[i][m][n];

       //two choices either include the current string or not
        int notTake = dfs(i - 1,m,n,strs);
        int take = 0;

         int zeros = 0,ones = 0;
            for(char c : strs[i].toCharArray())
            {
               if(c == '0') zeros++;
               else ones++;
            }

     //we can only include the current string if the zeros and ones are valid
         if(zeros <= m && ones <= n)
         {
             take = 1 + dfs(i -1,m - zeros,n - ones,strs);
         }
        //take max of both including and not including
         dp[i][m][n] = Math.max(take,notTake);

         return dp[i][m][n];
    }
    public int findMaxForm(String[] strs, int m, int n) {

          dp = new int[strs.length][m + 1][n + 1];
          //initially fill all cells with -1


          for(int[][] arr : dp)
            for(int[] row : arr)
              Arrays.fill(row,-1);

          return dfs(strs.length - 1,m,n,strs);  
    }
}
