```

class Solution {
    public int findMaxForm(String[] strs, int M, int N) {

        int[][] dp = new int[M + 1][N + 1];

        for(String str : strs)
        {
            int zeros = 0,ones = 0;
            for(char c : str.toCharArray())
            {
                if(c == '0') zeros++;
                else ones++;
            }
            for(int m = M;m >= zeros;m--)
            {
                for(int n = N;n >= ones;n--)
                {
                    dp[m][n] = Math.max(dp[m][n],1 + dp[m - zeros][n - ones]);
                }
            }
        }

        return dp[M][N];
    }
}

