        int notTake = dfs(i - 1,m,n,strs);
        int take = 0;

         int zeros = 0,ones = 0;
            for(char c : strs[i].toCharArray())
            {
               if(c == '0') zeros++;
               else ones++;
            }

     //we can only include the current string if the zeros and ones are valid
         if(zeros <= m && ones <= n)
         {
             take = 1 + dfs(i -1,m - zeros,n - ones,strs);
         }
        //take max of both including and not including
         dp[i][m][n] = Math.max(take,notTake);

         return dp[i][m][n];
    }
    public int findMaxForm(String[] strs, int m, int n) {

          dp = new int[strs.length][m + 1][n + 1];
          //initially fill all cells with -1


          for(int[][] arr : dp)
            for(int[] row : arr)
              Arrays.fill(row,-1);

          return dfs(strs.length - 1,m,n,strs);  
    }
}
