             for(int i = 0;i < s.length();i++)
             {
                 if(s.charAt(i) == '0') zeros++;
                 else ones++;
             } 
             if(zeros <= m && ones <= n)
               dp[0][m][n] = 1;
          }
      }
        for(int i = 1;i <strs.length;i++)
        {
            String s = strs[i];

            int zeros = 0,ones = 0;

            for(char c : s.toCharArray())
             {
                 if(c == '0') zeros++;
                 else ones++;
             }

            for(int m = 0;m <= M;m++)
            {
                for(int n = 0;n <= N;n++)
                {
                    int notTake = dp[i - 1][m][n];
                    int take = 0;
                    if(zeros <= m && ones <= n)
                    {
                        take = 1 + dp[i - 1][m - zeros][n -ones];
                    }
                    dp[i][m][n] = Math.max(take,notTake);
                }
            } 

        }
            return dp[strs.length - 1][M][N]; 

    }
