    int f(vector<pair<int,int>> &v,int i,int m,int n){

        if(i==v.size() or (m==0 and n==0)) return 0;

        int &ans=dp[i][m][n];

        if(ans!=-1) return ans;

        if(v[i].first<=m and v[i].second<=n) 
            ans=1+f(v,i+1,m-v[i].first,n-v[i].second);
        ans=max(ans,f(v,i+1,m,n));
