class Solution {
    public int findMaxForm(String[] strs, int m, int n) {
        return helper(strs, m, n, 0);
    }

    public int helper(String[] strs, int m, int n, int idx) {
        if(m < 0 || n < 0) {
            return Integer.MIN_VALUE;
        }
        if(idx == strs.length) {
            if(m >= 0 && n >= 0) {
                return 0;
            } 
            return Integer.MIN_VALUE;
        }
        int ones = 0, zeroes = 0;
        for(char ch: strs[idx].toCharArray()) {
            if(ch == '0') {
                zeroes++;
            } else {
                ones++;
            }
        }
        int inc = helper(strs, m - zeroes, n - ones, idx + 1) + 1;
        int exc = helper(strs, m, n, idx + 1);

        return Math.max(inc, exc);
    }
}
