class Solution {
    public int findMaxForm(String[] strs, int m, int n) {
        // DP - 0/1 knapsack, because we have 2x limitations m and n, so it needs 3-dimentional DP memo
        int size = strs.length;
        int[][][] memo = new int[size + 1][m + 1][n + 1];
        int[] strs0 = new int[size];
        int[] strs1 = new int[size];
	// calculate the number of 0’s and 1’s for each string
        for(int i = 0; i < size; i++) {
            char[] chs = strs[i].toCharArray();
            int sum0 = 0, sum1 = 0;
            for (char ch:chs) {
                if (ch == '0')
                    sum0 ++;
                else if (ch == '1')
                    sum1 ++;
            }
            strs0[i] = sum0;
            strs1[i] = sum1;
        }

        // populate the 3D memo
        for (int i = 1; i <= size; i++) {
            for (int j = 0; j <= m; j++) {
                for (int k = 0; k <= n; k++) {

                    if (j - strs0[i - 1] < 0 || k - strs1[i - 1] < 0) 
                        memo[i][j][k] = memo[i - 1][j][k];
                    else
                        memo[i][j][k] = Math.max(memo[i - 1][j][k], 1 + memo[i - 1][j - strs0[i - 1]][k - strs1[i - 1]]);
                }
            }
        }

        return memo[size][m][n];
    }
}

