**0-1 Knapsack application. Pick and Not pick each element of the vector.**
```
class Solution {
public:
    int dp[601][101][101];
    int knapsack(vector<string>& v, int l, int m, int n){
        if(l==0){
            return 0;
        }

        if(dp[l][m][n]!=-1){
            return dp[l][m][n];
        }

        int size=v[l-1].size();
        int zeroes=0,ones=0;
        for(int i=0;i<size;i++){
            if(v[l-1][i]=='0'){
                zeroes++;
            }else{
                ones++;
            }
        }

        if(m>=zeroes and n>=ones){
            return dp[l][m][n]=max(1+knapsack(v,l-1,m-zeroes,n-ones),knapsack(v,l-1,m,n));
        }else{
            return dp[l][m][n]=knapsack(v,l-1,m,n);
        }

    }
    int findMaxForm(vector<string>& strs, int m, int n) {
        int l=strs.size();
        memset(dp,-1,sizeof(dp));
        return knapsack(strs, l, m, n);
    }
};
