            for (int k = 0; k <= n; ++k) {
                if (counts[0] > j || counts[1] > k) {
                    cache[i][j][k] = cache[i+1][j][k];
                } else {
                    cache[i][j][k] = max(1 + cache[i+1][j-counts[0]][k-counts[1]], cache[i+1][j][k]);
                }
            }
        }
    }

    return cache[0][m][n];
}
