class Solution {
public:
    int findMaxForm(vector<string>& strs, int m, int n) {


        auto find = [](string s)
        {
            pair<int, int> p;
            for (auto i : s)
            {
                if (i == '0')
                    p.first++;
                else
                    p.second++;
            }
            return p;
        };

        vector<vector<int>> dp(m + 1, vector<int>(n + 1));

        for (auto s : strs)
        {
            pair<int, int> p = find(s);
            for (int i = m; i >= p.first; i--)
            {
                for (int j = n; j >= p.second; j--)
                {
                    dp[i][j] = max(dp[i][j], dp[i - p.first][j - p.second] + 1);
                }
            }
        }
        return dp[m][n];
    }
};
