class Solution {
public:
    int findMaxForm(vector<string>& strs, int m, int n) {
        int s=strs.size();
        int t[m+1][n+1][s+1];
        for(int i=0; i<=m; i++){
            for(int j=0; j<=n; j++){
                for(int k=0; k<=s; k++){
                    if((i==0 && j==0) || k==0){
                        t[i][j][k]=0;
                        continue;
                    }
                    string x=strs[k-1];
                    int z=0,o=0;
                    z=count(x.begin(),x.end(),'0');
                    o=x.size()-z;
                    if(o<=j && z<=i)
                        t[i][j][k]=max(1+t[i-z][j-o][k-1] , t[i][j][k-1]);
                    else
                        t[i][j][k]=t[i][j][k-1];
                }
            }
        }
        return t[m][n][s];
    }
};
