void knapsack(int c, vector<int> &a){
	int n=a.size();
    int t[c+1][n+1];
    for(int i=0; i<=c; i++){
        for(int j=0; j<=n; j++){
            if(i==0 || j==0){
                t[i][j]=0;
                continue;
            }
            if(a[j-1]<=i)   //if value of the element is less than the capacity <=> we have two choices : (1)Accept (2)Ignore
                t[i][j] = max(1+t[i-a[j-1]][j-1]] , t[i][j-1]);    //if element accepted <=> capacity to be reduced by the value of that element and move to the next element else capacity remains same and move to the next element
            else
                t[i][j]=t[i][j-1];   //value of the element is more than the capacity <=> ignore the element and move to the next element also, capacity remains unchanged
        }
    }
    cout<<"\nMax. number of elements to be chosen from the Knapsack = "<<t[c][n];
}
