class Solution:
    def findMaxForm(self, strs: List[str], m: int, n: int) -> int:
        # m zeros
        # n ones
        dp = [[0 for j in range(m+1)] for i in range(n+1)]
        for s in strs:
            ones = s.count('1')
            zeros = s.count('0')
            for i in range(n,ones-1,-1):
                for j in range(m,zeros-1,-1):
                    dp[i][j] = max(dp[i][j],dp[i-ones][j-zeros]+1)
        return dp[n][m]

