class Solution {
    public int dp [][][];
    public int findMaxForm (String[] strs, int m, int n) {
        dp = new int [m+1][n+1][strs.length+1];
        for (int matrix [][] : dp) {
            for (int row [] : matrix)
                Arrays.fill (row, -1);
        }
        return Solve (strs, m, n, 0);
    }

    public int Solve (String str [], int m, int n, int index) {
        if (str.length == 0 || (m + n) == 0 || m < 0 || n < 0 || index == str.length)
            return 0;
        if (dp [m][n][index] != -1)
            return dp [m][n][index];
        String curr = str [index];
        int zeros = getZerosCount (curr);
        int ones = curr.length () - zeros;
        if (m - zeros >= 0 && n - ones >= 0)
            dp [m][n][index] = Math.max (1 + Solve (str, m - zeros, n - ones, index + 1), Solve (str, m, n, index + 1));
        else
            dp [m][n][index] = Solve (str, m, n, index + 1);
        return dp [m][n][index];

    }

    public int getZerosCount (String curr) {
        int count = 0;
        for (int i = 0; i < curr.length (); i++) {
            if (curr.charAt (i) == '0')
                count++;
        }
        return count;
    }
}
