// method-1: Top-down approach
class Solution {
public:
    int findMaxForm(vector<string>& strs, int m, int n) {
        vector<vector<vector<int>>> dp(strs.size(), vector<vector<int>>(m+1, vector<int>(n+1, -1)));
        return helper(strs, 0, m, n, dp);
    }

    int helper(vector<string>& strs, int i, int m, int n, vector<vector<vector<int>>>& dp){
        if(i>=strs.size()){
            return 0;
        }
        if(m==0 && n==0){
            return 0;
        }
        if(dp[i][m][n]!=-1){
            return dp[i][m][n];
        }
        int m1=0;
        int n1=0;
        for(char c: strs[i]){
            if(c=='0'){
                m1++;
            }else{
                n1++;
            }
        }
        int ans=0;
        if(m1<=m && n1<=n){
            ans = max(ans, 1+ helper(strs,i+1, m-m1, n-n1, dp));
        }
        ans = max(ans, helper(strs,i+1, m, n, dp));
        dp[i][m][n] = ans;
        return ans;
    }
};
// method-2: Bottom-up approach
class Solution {
public:
    int findMaxForm(vector<string>& strs, int m, int n) {
        vector<vector<vector<int>>> dp(strs.size()+1, vector<vector<int>>(m+1, vector<int>(n+1, 0)));
        for(int i=1;i<=strs.size();i++){
            for(int j=0;j<=m;j++){
                for(int k=0;k<=n;k++){
                    int m1=0;
                    int n1=0;
                    for(char c: strs[i-1]){
                        if(c=='0'){
                            m1++;
                        }else{
                            n1++;
                        }
                    }
                    if(j>=m1 && k>=n1){
                        dp[i][j][k] = max(dp[i][j][k], 1 + dp[i-1][j-m1][k-n1]);
                        dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][k]);
                    }else{
                        dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][k]);
                    }
                }
            }   
        }
        return dp[strs.size()][m][n];
    }
};

// method-3: Bottom-up approach, memory optimisation
