                    for(char c: strs[i-1]){
                        if(c=='0'){
                            m1++;
                        }else{
                            n1++;
                        }
                    }
                    if(j>=m1 && k>=n1){
                        dp[i][j][k] = max(dp[i][j][k], 1 + dp[i-1][j-m1][k-n1]);
                        dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][k]);
                    }else{
                        dp[i][j][k] = max(dp[i][j][k], dp[i-1][j][k]);
                    }
                }
            }   
        }
        return dp[strs.size()][m][n];
    }
};

