            if(i == '1')
                one++;
        }
        return one;
    }
    int findMaxForm(vector<string>& strs, int m, int n) {
        int len = strs.size();
        int dp[len + 1][m + 1][n + 1];
        memset(dp, 0, sizeof(dp));
        for(int i = 1; i <= len; i++) {
            for(int j = 0; j <= m; j++) {
