class Solution {
public:
    pair<int,int> countF(string s)
    {
        int count=0;
        for(int i=0;i<s.length();i++)
        {
            count+= s[i]=='1';
        }
        return {count,(int)s.length()-count};
    }
    int findMaxForm(vector<string>& strs, int m, int n) {
        vector<pair<int,int> >count;
        for(int i=0;i<strs.size();i++)
        {
            count.push_back(countF(strs[i]));
        }
        int dp[(int)strs.size()+1][m+1][n+1];
        memset(dp, 0, sizeof dp);
        for(int i=0;i<=strs.size();i++)
        {
            for(int j=0;j<=m;j++)
            {
                for(int k=0;k<=n;k++)
                {
                    if(i==0)
                    {
                        dp[i][j][k]=0;
                        continue;
                    }
                    int count1=count[i-1].first;
                    int count0=count[i-1].second;
                    dp[i][j][k]=dp[i-1][j][k]; 
                    if(j >= count0 && k >= count1)
                    {
                       dp[i][j][k]=max(dp[i][j][k], dp[i-1][j-count0][k-count1]+1); 
                    }
                }
            }
        }
        return dp[(int)strs.size()][m][n];
    }
};
