class Solution {
public:
    map<pair<int,pair<int,int>>,int>mp;
    int dfs(vector<pair<int,int>>&v,int m,int n,int pos){
        if(mp.find({pos,{m,n}})!=mp.end())return mp[{pos,{m,n}}];
        int x=0;
        for(int i=pos;i<v.size();i++){
            if(m-v[i].first>=0 && n-v[i].second>=0){
                x=max(x,dfs(v,m-v[i].first,n-v[i].second,i+1)+1);
            }
        }
        mp[{pos,{m,n}}]=x;
        return x;
    }
    int findMaxForm(vector<string>& strs, int m, int n) {
        vector<pair<int,int>>v;
        for(int i=0;i<strs.size();i++){
            int zero=0,one=0;
            for(char ch:strs[i]){
                if(ch=='0')zero++;
                else one++;
            }
            v.push_back({zero,one});
        }
        return dfs(v,m,n,0);
    }
};
