class Solution {
public:
    int findComplement(int num) {
        //xor with 1111...11111

        //finding highest power of 2 in num
        int i=31;
        for (; i>=0; i--){
            if (pow(2, i) <= num) break; 
        }


        //making decimal representation of 111....1111
        int xorOp=0; 
        for (int j=i; j>=0; j--) xorOp += pow(2, j);


        return num^xorOp;
    }
};
