class Solution {
    public int findComplement(int num) {
        int i = 0, res = 0;
        while(num > 0){
            // if last bit of num is 0, then we need to take complement of it
            // so, add corresponding pow to res.
            if((1 & num) == 0) res += Math.pow(2, i);
            i++;
            num = num >> 1;
        }
        return res;
    }
}

