class Solution {
public:

    struct Node{
        Node* link;
        int value[2]= {0};

        bool containsKey(){
            return (link != NULL);
        }

        void put(Node* node){
            link = node;
        }

        Node* get(){
            return link;
        }

        void putvalue(char ch){
            ++value[ch - '0'];
        }

        int getvalue(char ch){
            return (ch == '0')?value[1]:value[0];
        }
    };

    class Trie{
        Node* root;
        public:

        Trie(){
            root = new Node();
        }

        void insert(string word){
            Node* node = root;
            for(int i = 0 ; i < word.size() ; i++){
                if(!node->containsKey()){
                    node->put(new Node());
                }
                node->putvalue(word[i]);
                node = node->get();
            }
        }

        int totalvalue(string word){
            Node* node = root;
            int total = 0;
            for(int i=0 ; i<word.size() ; i++){
                total += node->getvalue(word[i]);
                node = node->get();
            }
            return total;
        }

    };

    int totalHammingDistance(vector<int>& nums) {
        Trie *obj = new Trie();
        int n = nums.size();
        vector<string>v;
        for(int i = 0 ; i < n ; i++){
            string temp = "";
            int val = nums[i];
            while(val > 0){
                int value = val % 2;
                temp += ('0' + value);
                val = val / 2;
            }
            // cout<<temp<<" ";
            while(temp.size() < 32){
                temp += '0';
            }
            v.push_back(temp);
            // cout<<temp<<endl;
        }

        for(int i=0 ; i<v.size() ; i++){
            obj->insert(v[i]);
        }

        int ans = 0;
        for(int i=0 ; i<v.size() ; i++){
            int val = obj->totalvalue(v[i]);
            ans += val;
        }

        return ans/2;
    }
};

