```
/**
 * Bitwise Solution:
 *
 *     0 0 1 0 (2)
 *     1 1 0 1 (13)
 *     1 0 0 0 (8)
 *     1 0 1 1 (11)
 *           ^ Num of 1's = 2, Num of 0's = 2, HammingDistance = 2 * 2 = 4
 *         ^ Num of 1's = 2, Num of 0's = 2, HammingDistance = 2 * 2 = 4
 *       ^ Num of 1's = 1, Num of 0's = 3, HammingDistance = 1 * 3 = 3
 *     ^ Num of 1's = 3, Num of 0's = 1, HammingDistance = 3 * 1 = 3
 *
 *   Total = 4 + 4 + 3 + 3 = 14
*/

class Solution {
    public int totalHammingDistance(int[] nums) {

        int oneCnt;
        int result = 0;
        for(int i=0; i<32; i++){
            oneCnt = 0;
            for(int j=0; j<nums.length; j++){
                //Java Bitwise Operator
                // nums[j] >> i:  (Bitwise shift) shift i bit to the right
                //                  1110 >> 0 = 1110
                //                  1110 >> 1 = 0111
                //                  
                // & 1: (Bitwise And) check the last bit is 1
                //                  1110 & 1 is actually 1110 & 0001 = 0000 = 0
                //                  0111 & 1 is actually 0111 & 0001 = 0001 = 1

                oneCnt += (nums[j] >> i) & 1;
            }
            result += oneCnt * (nums.length - oneCnt);
        }

        return result;

    }


}
```

