class Solution {
public:
    int totalHammingDistance(vector<int>& nums) 
    {
        int n = nums.size();
        vector<int> count(32, 0);

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<32; j++)
            {
			// we are just storing every set bit of each number in the count array.
                if((nums[i]&(1<<j)) != 0)
                count[j] += 1;
            }
        }
		// after saving all set bits in count array, we have got the count 
		// of all set bits of every number
		// like in example nums[] = {4, 14, 2}
		// count array will be like {0, 2, 2, 1, 0 ... }
		// means for every bit,
		// count[i] numbers will make pair with the n-count[i] numbers
        int ans=0;
        for(int i=0; i<32; i++)
        {
            ans += count[i] * (n-count[i]);
        }
        return ans;
		// Hope it helps you ;)
    }
};
