class Solution {
    public int totalHammingDistance(int[] nums) {
        //brute force time o(n^2)
        //algo - iterate through every posible pairs and find their onbits 
        //by xor and then bitcount
        int sum=0;
        int n = nums.length;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                int bitcount =0;
                int xor = nums[i] ^ nums[j];
                int mask = 1;
                //or we can use Integer.bitCount(xor)
                while(mask!=0)
                {
                    int d = xor & mask;
                    if(d != 0)
                        bitcount++;
                    mask = mask << 1;
                }
                sum+=bitcount;
            }
        }
        return sum;

        //---------

        //time o(32*n) = o(n)
        //as we are checking every pair why not check at the same time for 
        //every element.
        //so we will check every bit of every elem same time then find the
        //on bit and off bit and the difference will be multiple of on and 
        //off bit.
        //example 1 - 0100 - 4
        //            1110 - 14
        //            0010 - 2
        //starting from rightmost(3) bit all bits are same.
        //then for(2) only one bit is different and that is 0 other s are 1
        //here on bit count is 2 and off bit count is 1 so total difference 
        //will be 2*1 = 2 , bcz both 2 and 14 can be pair with 4.
        //repeate this for all bits then sum the diff.
        int n = nums.length;
        int sum = 0;
        for(int i=0;i<32;i++){
            int countbiton =0 , countbitoff = 0 , diff = 0;
            for(int elm:nums){
                if((elm & (1<<i)) != 0 ) // check on or off with bitmask
                    countbiton++;
            }
            countbitoff = n-countbiton;
            diff = countbitoff*countbiton;
            sum+=diff;
        }
        return sum;
    }
}
