class Solution {
    func rotate(_ mat: inout [[Int]]) {
        let len = mat.count
        for i in 0..<len {
            for n in i..<len {
                let tmp = mat[i][n]
                mat[i][n] = mat[n][i]
                mat[n][i] = tmp
            }
            for n in 0..<(len / 2) {
                let tmp = mat[i][n], l = len
                mat[i][n] = mat[i][l - 1 - n]
                mat[i][l - 1 - n] = tmp
            }
        }
    }
}
