    public void rotate(int[][] matrix) {

        //1.Transpose
        for(int r=0;r<matrix.length;r++){
            for(int c=0;c<r;c++){
               int temp=martrix[r][c];
               matrix[r][c]=matrix[c][r];
               matrix[c][r]=temp;
            }
