void rotate(int** matrix, int matrixSize, int* matrixColSize){
    // Swap process..
    for(int row = 0; row < matrixSize; row++) {
        for(int col = row; col < matrixSize; col++) {
            int tmp = matrix[row][col];
            matrix[row][col] = matrix[col][row];
            matrix[col][row] = tmp;
        }
