    let rotatedMatrix = new Array(n).fill(0).map(_ => new Array(m).fill(0));

    for(let i=0; i < m; i++){
        for(let j=0; j < n; j++){
            rotatedMatrix[n-j-1][i] = matrix[i][j];
        }
    }

    return rotatedMatrix;
}

