class Solution {
public:
    void rotate(vector<vector<int>>& matrix) {
	    // Brute Force Solution Time O(N^2) & Auxiliary Space O(N^2)
        int len=matrix.size();
        vector<vector<int>> B;
        B=matrix;
        for(int i=0;i<len;i++){
            for(int j=0;j<len;j++){
                B[j][len-i-1]=matrix[i][j];
            }
        }
        matrix=B;  
    }     
};
```
