class Solution:
    def rotate(self, matrix: List[List[int]]) -> None:
        m, n = len(matrix), len(matrix[0])

        def transpose():
            for r in range(m):
                for c in range(r + 1, n):
                    matrix[r][c], matrix[c][r] = matrix[c][r], matrix[r][c]

        def reverse():
            for r in range(m):
                for c in range(n//2):
                    matrix[r][c], matrix[r][~c] = matrix[r][~c], matrix[r][c]

        transpose()
        reverse()
