    void rotate(vector<vector<int>>& matrix) {
        int n=matrix.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<=i;j++){
                swap(matrix[i][j],matrix[j][i]);
            }
        }
        for(int i=0;i<n;i++){
            int j=0;
            int k=n-1;
            while(j<=n/2 && k>=n/2){
                swap(matrix[i][j],matrix[i][k]);
                j++;
                k--;
            }
        }
    }
};

for (int i = 0; i < matrix.length; i++) {
    List<int> subList = [];
    for (int j = matrix.length - 1; j >= 0; j--) {
      subList.add(matrix[j][i]);
    }
    output.add(subList);
  }

T.C = O( R*C )
S.C = O(1) [ in place ]
