class Solution {
    // Stores a list of all possible IPs
    LinkedList<String> result = new LinkedList<String>();

    // Stores the current IP being evaluated.
    // Each item in this list is a number to be joined by "." to form an IP address.
    LinkedList<String> temp = new LinkedList<String>();

    private void backtrack(String input, int start, int length) {
        int n = input.length();

        // If the collective string length of temp is equal to the length of input & temp has 4 different strings, then add temp to result
        if(length == input.length() && temp.size() == 4) {
            result.add(String.join(".", temp));
        }
        // If temp size is greater than 4 its not a valid IP
        else if(temp.size() >= 4) {
            return;
        }

        // Iteratively add the next 3 characters to temp
        for(int i = 1; i <= 3; i++) {
            if(start + i > n) break;

            // Parse the next 1-3 characters
            String nextChars = input.substring(start, start+i);

            // Validate the value
            if(!isValid(nextChars)) continue;

            // Add the next few chars to temp
            temp.add(nextChars);

            // Recurse after choosing a group of characters to include
            backtrack(input, start+i, length+nextChars.length());

            // Remove the few chars from temp that we just added to obtain other possible solutions
            temp.removeLast();
        }
    }

    // Returns true if s is a valid integer to form an IP address
    private boolean isValid(String s) {
        if(s.length() > 1 && s.charAt(0) == '0') {
            return false;
        }

        return Integer.parseInt(s) <= 255;
    }


    public List<String> restoreIpAddresses(String s) {
        int n = s.length();
        if(n == 0 || n > 12) return result;
        backtrack(s, 0, 0);

        return result;
    }
}
