class Solution {
public:

    bool isValid(string &s)
    {
        int n=s.size();

        //base case
        if(n==1){
            return true;
        }
        //if length >3 or string starts with 0
        if(n>3||s[0]=='0'){
            return false;
        }
        // converting string to integer to check if it is less than equal to 255
        int val=stoi(s);
        if(val>255){
            return false;
        }
        return true;
    }

    // void solve(string &s, vector<string>&ans)
    // {

    // }

    vector<string> restoreIpAddresses(string s) {
        vector<string>ans;

        // base case
        if(s.size()>12)
        return ans;

        // divide string into 4 parts, each having length=3
        // iterate over each part & form corresponding substrings

        for(int i=1;i<=3;i++) // 1st part
        {
            for(int j=1;j<=3;j++) // 2nd part
            {
                for(int k=1;k<=3;k++) // 3rd part
                {
                    if((i+j+k<s.size()) and (i+j+k+3>=s.size())) // check if last part is of length 3
                    {
                        string a=s.substr(0,i);
                        string b=s.substr((i+0),j);
                        string c=s.substr((j+i),k);
                        string d=s.substr(i+j+k);

                        // if each substring is valid, then push into array
                        if(isValid(a) and isValid(b) and isValid(c) and isValid(d))
                        ans.push_back(a+"."+b+"."+c+"."+d);
                    }
                }
            }
        }

        return ans;
    }
};
